refsynopsisdiv.synopsis:
#define             CAIRO_HAS_IMAGE_SURFACE
enum                cairo_format_t;
int                 cairo_format_stride_for_width       (cairo_format_t format,
                                                         int width);
cairo_surface_t *   cairo_image_surface_create          (cairo_format_t format,
                                                         int width,
                                                         int height);
cairo_surface_t *   cairo_image_surface_create_for_data (unsigned char *data,
                                                         cairo_format_t format,
                                                         int width,
                                                         int height,
                                                         int stride);
unsigned char *     cairo_image_surface_get_data        (cairo_surface_t *surface);
cairo_format_t      cairo_image_surface_get_format      (cairo_surface_t *surface);
int                 cairo_image_surface_get_width       (cairo_surface_t *surface);
int                 cairo_image_surface_get_height      (cairo_surface_t *surface);
int                 cairo_image_surface_get_stride      (cairo_surface_t *surface);

refsect2.programlisting:
typedef enum {
    CAIRO_FORMAT_INVALID   = -1,
    CAIRO_FORMAT_ARGB32    = 0,
    CAIRO_FORMAT_RGB24     = 1,
    CAIRO_FORMAT_A8        = 2,
    CAIRO_FORMAT_A1        = 3,
    CAIRO_FORMAT_RGB16_565 = 4,
    CAIRO_FORMAT_RGB30     = 5
} cairo_format_t;

informalexample.programlisting:
int stride;
unsigned char *data;
cairo_surface_t *surface;

stride = cairo_format_stride_for_width (format, width);
data = malloc (stride * height);
surface = cairo_image_surface_create_for_data (data, format,
					  width, height,
					  stride);

refsect2.programlisting:
cairo_surface_t *   cairo_image_surface_create          (cairo_format_t format,
                                                         int width,
                                                         int height);

listitem.simpara:
a pointer to the image data of this surface or NULL
if surface is not an image surface, or if cairo_surface_finish()
has been called.
